mapper.select:
SELECT A.blog_id , A.user_id , A.blog_nm , A.blog_con , A.del_yn , A.rgsn_user_id , A.rgsn_ts , A.amnn_user_id , A.amnn_ts , coalesce(B.total_blog_views_cnt, 0) AS total_blog_views_cnt , coalesce(C.today_blog_views_cnt, 0) AS today_blog_views_cnt
FROM blog_info_m A
/* 블로그 전체 방문자 수 */ LEFT JOIN ( SELECT blog_id, COUNT(0) AS total_blog_views_cnt FROM blog_views_l GROUP BY blog_id ) B ON B.blog_id = A.blog_id
/* 블로그 오늘 방문자 수 */ LEFT JOIN ( SELECT blog_id, COUNT(0) AS today_blog_views_cnt FROM blog_views_l WHERE DATE_TRUNC('day', rgsn_ts) = CURRENT_DATE GROUP BY blog_id ) C ON C.blog_id = A.blog_id
WHERE A.blog_id = #{blogId}

mapper.update:
UPDATE
    blog_info_m
SET
blog_nm       = #{blogNm}
, blog_con      = #{blogCon}
, amnn_user_id  = #{userId}
, amnn_ts       = CURRENT_TIMESTAMP

WHERE 1 = 1
AND user_id = #{userId}

mapper.select:
SELECT COUNT(1)
FROM blog_views_l
WHERE blog_id = #{blogId}
  AND rgsn_user_id = #{userId}

mapper.insert:
INSERT INTO blog_views_l
(
  blog_views_id
, blog_id
, rgsn_user_id
, rgsn_ts
)
VALUES
    (
      #{userId}
    , #{blogId}
    , #{userId}
    , CURRENT_TIMESTAMP
    )
