mapper.select:
SELECT A.blog_post_comment_id , A.blog_post_id , A.blog_comment_con , A.blog_parent_comment_id , A.del_yn , A.rgsn_user_id , RGSN.user_nm AS rgsn_user_nm , A.rgsn_ts , A.amnn_user_id , AMNN.user_nm AS amnn_user_nm , A.amnn_ts
FROM blog_post_comment_l A
/* 등록/수정 사용자 정보 */ LEFT JOIN blog_user_info_m RGSN ON RGSN.user_id = A.rgsn_user_id LEFT JOIN blog_user_info_m AMNN ON AMNN.user_id = A.amnn_user_id
WHERE 1 = 1
AND A.blog_post_id = #{blogPostId}
/* 부모 ID 가 검색조건으로 존재하는 경우, 부모ID 검색 그렇지 않은 경우 부모ID 가 NULL 인 댓글 조회*/
AND A.blog_parent_comment_id = #{blogParentCommentId}
AND A.blog_parent_comment_id IS NULL
ORDER BY A.rgsn_ts
LIMIT #{record} OFFSET (#{page} - 1) * #{record}

mapper.insert:
INSERT INTO blog_post_comment_l
(
  blog_post_comment_id
, blog_post_id
, blog_comment_con
, blog_parent_comment_id
, del_yn
, rgsn_user_id
, rgsn_ts
, amnn_user_id
, amnn_ts

)
VALUES
    (
      #{blogCommentId}
    , #{blogPostId}
    , #{blogCommentCon}
    , #{blogParentCommentId}
    , 'N'
    , #{rgsnUserId}
    , CURRENT_TIMESTAMP
    , #{amnnUserId}
    , CURRENT_TIMESTAMP
    )
