mapper.select:
SELECT blog_post_tag_id , blog_post_id , blog_tag_con , del_yn , rgsn_user_id , rgsn_ts , amnn_user_id , amnn_ts
FROM blog_post_tag_l
WHERE 1 = 1 AND blog_post_id = #{blogPostId}

mapper.insert:
insert into blog_post_tag_l
(
  blog_post_tag_id
, blog_post_id
, blog_tag_con
, del_yn
, rgsn_user_id
, rgsn_ts
, amnn_user_id
, amnn_ts
)
values
    (
      #{blogPostTagId}
    , #{blogPostId}
    , #{blogTagCon}
    , 'N'
    , #{rgsnUserId}
    , CURRENT_TIMESTAMP
    , #{amnnUserId}
    , CURRENT_TIMESTAMP
    )

mapper.update:
UPDATE
blog_post_tag_l
SET
del_yn = 'Y'
, amnn_user_id = #{amnnUserId}
, amnn_ts = CURRENT_TIMESTAMP
WHERE 1 = 1
AND blog_post_id = #{blogPostId}

    AND blog_post_tag_id = #{blogPostTagId}
